// Kirish ma'lumotlari
const inputBody = $('When Executed by Another Workflow').all()[0].json.cycles;

// type: 1 => shift_rest
// type: 0 => shift
const type = inputBody.type;
const events = inputBody.events || [];

// Event mapping obyekti
const eventMapping = {
    1: {
        1: ["DS_OFF", "Ishdan tashqari"],
        2: ["DS_SB", "Uxlayapti yoki dam"],
        3: ["DS_D", "Haydash jarayonida"],
        4: ["DS_ON", "Ish holati, haydamaydi"],
    },
    2: {
        1: ["INTER_NORMAL_PRECISION", "To‘liq joylashuv"],
        2: ["INTER_REDUCED_PRECISION", "Noaniq joylashuv"],
    },
    3: {
        1: ["PS_USE", "Shaxsiy foydalanish"],
        2: ["YRD_MOVE", "Yard ichida harakat"],
        0: ["PS_CLEAR", "Shaxsiy rejim bekor"],
    },
    4: {
        1: ["DR_CERT_1", "1-tasdiqlash"],
        2: ["DR_CERT_2", "2-tasdiqlash"],
        3: ["DR_CERT_3", "3-tasdiqlash"],
        4: ["DR_CERT_4", "4-tasdiqlash"],
        5: ["DR_CERT_5", "5-tasdiqlash"],
        6: ["DR_CERT_6", "6-tasdiqlash"],
        7: ["DR_CERT_7", "7-tasdiqlash"],
        8: ["DR_CERT_8", "8-tasdiqlash"],
        9: ["DR_CERT_9", "9-tasdiqlash"],
    },
    5: {
        1: ["DR_LOGIN", "Haydovchi tizimga kirdi"],
        2: ["DR_LOGOUT", "Haydovchi tizimdan chiqdi"],
    },
    6: {
        1: ["ENG_UP_NORMAL", "Motor ishga tushdi (GPS aniq)"],
        2: ["ENG_UP_REDUCED", "Motor ishga tushdi (GPS noaniq)"],
        3: ["ENG_DOWN_NORMAL", "Motor o‘chirildi (GPS aniq)"],
        4: ["ENG_DOWN_REDUCED", "Motor o‘chirildi (GPS noaniq)"],
    },
    7: {
        1: ["MALF_LOGGED", "Nosozlik qayd etildi"],
        2: ["MALF_CLEARED", "Nosozlik bartaraf qilindi"],
        3: ["DIAG_LOGGED", "Diagnostika xatosi bor"],
        4: ["DIAG_CLEARED", "Diagnostika tuzatildi"],
    }
};

// Eventlarni boyitish
const processedEvents = events.map(ev => {
    const type = ev.eventType;
    const code = ev.eventCode;

    let eventName = "";
    let eventDesc = "";

    if (eventMapping[type] && eventMapping[type][code]) {
        eventName = eventMapping[type][code][0];
        eventDesc = eventMapping[type][code][1];
    } else {
        eventName = ev.eventCodeDescription || `UNKNOWN_${type}_${code}`;
        eventDesc = ev.eventTypeDescription || "Noma'lum holat";
    }

    return {
        dateTime: ev.dateTime,
        durationInSeconds: ev.durationInSeconds,
        event: eventName,
        eventDescription: eventDesc,
        longitude: ev.longitude,
        latitude: ev.latitude,
        calculatedLocation: ev.calculatedLocation,
        manualLocation: ev.manualLocation || '',
        totalEngineHours: ev.totalEngineHours || 0,
        totalVehicleMiles: ev.totalVehicleMiles,
        originalEventType: type,
        originalEventCode: code
    };
});

// Natija obyekt nomi type asosida tanlanadi
const outputKey = type === 1 ? "shift_rest" : "shift";

// Yakuniy chiqish
return {
  "question":{
    [outputKey]: processedEvents}

};

let violations = [];
const inputItem = $input.first();

// 1. Kiruvchi ma'lumotni tekshirish
if (inputItem && inputItem.json && inputItem.json.output) {
    let rawText = inputItem.json.output;

    // JSONni tozalash (faqat kerakli qismini sug'urib olish)
    function cleanAndParse(text) {
        // Markdown bloklarini (```json ... ```) olib tashlash
        let clean = text.replace(/```json|```/gi, '').trim();

        // Agar AI matn qo'shib yuborgan bo'lsa, faqat { } orasini olish
        const start = clean.indexOf('{');
        const end = clean.lastIndexOf('}');
        const arrayStart = clean.indexOf('[');
        const arrayEnd = clean.lastIndexOf(']');

        if (start !== -1 && end !== -1 && (arrayStart === -1 || start < arrayStart)) {
            clean = clean.substring(start, end + 1);
        } else if (arrayStart !== -1 && arrayEnd !== -1) {
            clean = clean.substring(arrayStart, arrayEnd + 1);
        }

        return JSON.parse(clean);
    }

    try {
        const jsonOutput = cleanAndParse(rawText);

        // Turli formatlarni tekshirish
        if (jsonOutput.violations && Array.isArray(jsonOutput.violations)) {
            violations = jsonOutput.violations;
        } else if (jsonOutput.answer && Array.isArray(jsonOutput.answer)) {
            violations = jsonOutput.answer;
        } else if (Array.isArray(jsonOutput)) {
            violations = jsonOutput;
        } else if (typeof jsonOutput === 'object' && jsonOutput !== null) {
            violations = [jsonOutput];
        } else {
            throw new Error("Format aniqlanmadi");
        }

    } catch (error) {
        // Xato bo'lsa zaxira xabari
        violations = [{
            violation: "data_quality",
            dateTimeStart: new Date().toISOString(),
            dateTimeEnd: new Date().toISOString(),
            description: "JSON parse xatosi: " + error.message
        }];
    }
} else {
    violations = [{
        violation: "data_quality",
        description: "Kiruvchi ma'lumot topilmadi"
    }];
}

// n8n uchun to'g'ri qaytarish formati
return { violations };

